import React, { useCallback } from "react";
import { withRouter } from "react-router";
import Header from "../Components/header"
// import FirebaseApp from "../firebaseConfig"
import firebaseApp from "../firebaseConfig";
// import TextField from '@material-ui/core/TextField';
import firebase from "firebase/app";
import { Select } from "@material-ui/core";
// import { Select } from "@material-ui/core";
// import firestore from "firebase/firebase-firestore"
import PersonIcon from '@material-ui/icons/Person';

const Signup = function(){
    async function handleSignUp(event){
        event.preventDefault();
        const db = firebase.firestore();
        const { fullname,email,gender,college,designation,password } = event.target.elements;
        try {
            console.log("started")
            await firebaseApp
              .auth()
              .createUserWithEmailAndPassword(email.value, password.value).then(function(user){
                  console.log(user.user.uid);
                  db.collection("UserDetails").doc(user.user.uid).set({
                      FullName: fullname.value,
                      Email: email.value,
                      College: college.value,
                      Gender: gender.value,
                      Uid: user.user.uid,
                      Designation: designation.value
                  }).then(function() {
                    console.log("Document successfully written!");
                })

              })
              console.log("success")
          } catch (error) {
            alert(error);
          }
    }
    return (<div>
        <Header></Header>
        <div className="SignupDiv">
            <h1>Sign Up</h1>
            <form onSubmit={handleSignUp}>
            <input name="fullname" type="text" placeholder="Full Name"></input>
            <input name="email" type="email" placeholder="Email"></input>
            <select name="gender" placeholder="You are">
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
            </select>
            <PersonIcon></PersonIcon>
            <select name="college">
            <option value="VIT University">VIT University</option>
            <option value="GITAM University">GITAM University</option>
            <option value="SRM University">SRM University</option>
            </select>
            <select name="designation">
            <option value="Student">Student</option>
            <option value="Faculty">Faculty</option>
            <option value="Organization">Organization</option>
            </select>
            <input name="password" type="password" placeholder="Password"></input>
            <button type="submit" >Sign Up</button>
            </form>
        </div>
    </div>);
}

export default withRouter(Signup);


