import React from "react";
import Header from "../Components/header";
import Button from '@material-ui/core/Button';
import { Link } from "react-router-dom";
import { TextField } from "@material-ui/core";
import firebaseApp from "../firebaseConfig";

const Splash = ({ history }) => {
    async function handleSignin(event){
        event.preventDefault();
        const db = firebase.firestore();
        const { fullname,email,gender,college,designation,password } = event.target.elements;
        try {
            console.log("started")
            await firebaseApp
              .auth()
              .createUserWithEmailAndPassword(email.value, password.value).then(function(user){
                  console.log(user.user.uid);
                  db.collection("UserDetails").doc(user.user.uid).set({
                      FullName: fullname.value,
                      Email: email.value,
                      College: college.value,
                      Gender: gender.value,
                      Uid: user.user.uid,
                      Designation: designation.value
                  }).then(function() {
                    console.log("Document successfully written!");
                    // Redirect("")
                    history.push("/userdetails")
                })

              })
              console.log("success")
          } catch (error) {
            alert(error);
          }
    }
    return <div>
        <Header></Header>
        <div className="buttons">
        <h1>SignIn</h1>
        <form onSubmit={handleSignin}>
            <input name="email" type="email" placeholder="Email"></input>
            <input name="password" type="password" placeholder="Password"></input>
            <button type="submit" >Sign Up</button>
            </form>
        <h4>Don't have account?</h4>
        <Link to="/signup"><Button children="Sign up" size="large" variant="contained"  style={{borderRadius:20}} ></Button></Link> 
        </div>
    </div>
}
export default Splash;

