import React from "react";
import Header from "../Components/header";
import Button from '@material-ui/core/Button';
import { Link } from "react-router-dom";
import { TextField, AppBar } from "@material-ui/core";
import firebaseApp from "../firebaseConfig";
import { makeStyles } from '@material-ui/core/styles';
import { red, green } from "@material-ui/core/colors";
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
// import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


const Splash = ({ history }) => {
    async function handleSignin(event){
        event.preventDefault();
        const { email,password } = event.target.elements;
        try {
            console.log("started")
            await firebaseApp
              .auth()
              .signInWithEmailAndPassword(email.value,password.value).then((user) =>{
                  console.log(user);
                  history.push('/home')
              })
              console.log("success")
          } catch (error) {
            alert(error);
          }
    }
    // const classes = styles();
    return <div>
        <AppBar position="static">
  <Toolbar>
    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
      <MenuIcon />
    </IconButton>
    <Typography variant="h6" className={classes.title}>
      News
    </Typography>
    <Button color="inherit">Login</Button>
  </Toolbar>
</AppBar>
        <div className="buttons">
        <h1>SignIn</h1>
        <form onSubmit={handleSignin}>
            <input name="email" type="email" placeholder="Email"></input>
            <input name="password" type="password" placeholder="Password"></input>
            <button type="submit" >Sign In</button>
            </form>
        <h4>Don't have account?</h4>
        <Link to="/signup"><Button children="Sign up" size="large" variant="contained"  style={{borderRadius:20}} ></Button></Link> 
        <Button color="primary" variant="contained">Signin</Button>
        <TextField></TextField>
        </div>
    </div>
}
export default Splash;

