import React from "react";
import Header from "../Components/header";
import Button from '@material-ui/core/Button';
import { Link } from "react-router-dom";
import { TextField } from "@material-ui/core";
import firebaseApp from "../firebaseConfig";
import { makeStyles } from '@material-ui/core/styles';
import { red } from "@material-ui/core/colors";

const useStyles = makeStyles({
    root: {
    //   backgroundColor: 'red',
      color: red,
    },
  });

const Splash = ({ history }) => {
    async function handleSignin(event){
        event.preventDefault();
        const { email,password } = event.target.elements;
        try {
            console.log("started")
            await firebaseApp
              .auth()
              .signInWithEmailAndPassword(email.value,password.value).then((user) =>{
                  console.log(user);
                  history.push('/home')
              })
              console.log("success")
          } catch (error) {
            alert(error);
          }
    }
    const classes = useStyles();
    return <div>
        <Header></Header>
        <div className="buttons">
        <h1>SignIn</h1>
        <form onSubmit={handleSignin}>
            <input name="email" type="email" placeholder="Email"></input>
            <input name="password" type="password" placeholder="Password"></input>
            <button type="submit" >Sign In</button>
            </form>
        <h4>Don't have account?</h4>
        <Link to="/signup"><Button children="Sign up" size="large" variant="contained"  style={{borderRadius:20}} ></Button></Link> 
        <Button color="secondary" variant="contained">Signin</Button>
        <TextField color="secondary" variant="outlined" className={classes.root}></TextField>
        </div>
    </div>
}
export default Splash;

