import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Copyright from '../Components/copyright'
import firebase from "firebase/app";
import firebaseApp from "../firebaseConfig";
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(5),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: theme.palette.secondary.main,
    padding: theme.spacing(3),
    borderRadius: 30,

  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.primary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

const Signup = ({ history }) =>{
  const classes = useStyles();
  async function handleSignUp(event){
    event.preventDefault();
    const db = firebase.firestore();
    const { fullName,email,gender,college,designation,password,terms } = event.target.elements;
    try {
        console.log("started")
        console.log(terms.checked);
        console.log(gender.value);

        await firebaseApp
          .auth()
          .createUserWithEmailAndPassword(email.value, password.value).then(function(user){
              console.log(user.user.uid);
              db.collection("UserDetails").doc(user.user.uid).set({
                  FullName: fullName.value,
                  Email: email.value,
                  College: college.value,
                  Gender: gender.value,
                  Uid: user.user.uid,
                  Designation: designation.value
              }).then(function() {
                console.log("Document successfully written!");
                // Redirect("")
                // history.push("/userdetails")
                // history.goBack();
            })

          })
          console.log("success")
      } catch (error) {
        alert(error);
      }
}

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign up
        </Typography>
        <form className={classes.form} onSubmit={handleSignUp}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                autoComplete="fullName"
                name="fullName"
                variant="outlined"
                required
                fullWidth
                id="fullName"
                label="Full Name"
                autoFocus
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
              />
            </Grid>
            <Grid item xs={12}>
            <FormControl variant="outlined" fullWidth required>
        <InputLabel htmlFor="outlined-age-native-simple">Gender</InputLabel>
        <Select
        fullWidth
          native
          label="Age"
          inputProps={{
            name: 'gender',
            id: 'outlined-age-native-simple',
          }}
        >
          <option aria-label="None" value="" />
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Others">Others</option>
        </Select>
      </FormControl>
            </Grid>
            <Grid item xs={12}>
            <FormControl variant="outlined" fullWidth required>
        <InputLabel htmlFor="outlined-age-native-simple">You are</InputLabel>
        <Select
        fullWidth
          native
          label="You are"
          inputProps={{
            name: 'designation',
            id: 'outlined-age-native-simple',
          }}
        >
          <option aria-label="None" value="" />
          <option value="Student">Student</option>
          <option value="WorkingProfessional">Working Professional</option>
          <option value="Club/Organisation">Club/Organisation</option>
        </Select>
      </FormControl>
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="designation"
                label="You Are"
                name="designation"
                autoComplete="designation"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="college"
                label="Your College"
                type="text"
                id="college"
                autoComplete="college"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
              />
            </Grid>
            <Grid item xs={12}>
              <FormControlLabel
                control={<Checkbox color="primary" name="terms" />}
                label="I accept the terms and conditions"
              />
            </Grid>
          </Grid>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign Up
          </Button>
          <Grid container justify="center">
            <Grid item>
              <Link href="#" variant="body2">
                Already have an account? Sign in
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={2}>
        <Copyright />
      </Box>
    </Container>
  );
}

export default Signup;












