import React from "react";
import Header from "../Components/header";
import Button from '@material-ui/core/Button';
import { Link } from "react-router-dom";
import { TextField } from "@material-ui/core";
import firebaseApp from "../firebaseConfig";
import { makeStyles } from '@material-ui/core/styles';
import { red, green } from "@material-ui/core/colors";

// const useStyles = makeStyles({
//     root: {
//     //   backgroundColor: 'red',
//       color: red,
//       borderBlockColor:red,
//       borderTopColor: red,
//       borderColor: red,
//     },
//   });


  const styles = makeStyles({
    container: {
      display: 'flex',
      flexWrap: 'wrap',
      color: green
    },
    textField: {
    //   marginLeft: theme.spacing.unit,
    //   marginRight: theme.spacing.unit,
      width: 300,
      color: green
    },

    cssLabel: {
      color : 'green'
    },

    cssOutlinedInput: {
      '&$cssFocused $notchedOutline': {
        borderColor: green,
        color:green
      }
    },

    cssFocused: {
        color: green
    },

    notchedOutline: {
      borderWidth: '1px',
      borderColor: 'green !important',
      color:green
    },

  });

const Splash = ({ history }) => {
    async function handleSignin(event){
        event.preventDefault();
        const { email,password } = event.target.elements;
        try {
            console.log("started")
            await firebaseApp
              .auth()
              .signInWithEmailAndPassword(email.value,password.value).then((user) =>{
                  console.log(user);
                  history.push('/home')
              })
              console.log("success")
          } catch (error) {
            alert(error);
          }
    }
    const classes = styles();
    return <div>
        <Header></Header>
        <div className="buttons">
        <h1>SignIn</h1>
        <form onSubmit={handleSignin}>
            <input name="email" type="email" placeholder="Email"></input>
            <input name="password" type="password" placeholder="Password"></input>
            <button type="submit" >Sign In</button>
            </form>
        <h4>Don't have account?</h4>
        <Link to="/signup"><Button children="Sign up" size="large" variant="contained"  style={{borderRadius:20}} ></Button></Link> 
        <Button color="secondary" variant="contained">Signin</Button>
        <TextField id="standard-name"
          label="Name"
          className={classes.textField}

          margin="normal"
          variant="standard"
          InputLabelProps={{
            classes: {
              root: classes.cssLabel,
              focused: classes.cssFocused,
            },
          }}
          InputProps={{
            classes: {
              root: classes.cssOutlinedInput,
              focused: classes.cssFocused,
              notchedOutline: classes.notchedOutline,
            },
            inputMode: "numeric"
          }}></TextField>
        </div>
    </div>
}
export default Splash;

