import React, { useCallback } from "react";
import { withRouter } from "react-router";
import Header from "../Components/header"
// import FirebaseApp from "../firebaseConfig"
import firebaseApp from "../firebaseConfig";
// import TextField from '@material-ui/core/TextField';
import firebase from "firebase/app";
import firestore from "firebase/firebase-firestore"

const Signup = function(){
    async function handleSignUp(event){
        event.preventDefault();
        const { fullname,email,gender,college,designation,password } = event.target.elements;
        try {
            console.log("started")
            await firebaseApp
              .auth()
              .createUserWithEmailAndPassword(email.value, password.value).then(function(user){
                  console.log(user.user.uid);
                  const db = firebase.firestore;
                  db.collection("UserDetails").doc(user.user.uid).set({
                      FullName: fullname.value,
                      Email: email.value,
                      College: college.value,
                      Gender: gender.value,
                      Uid: user.user.uid,
                      Designation: designation.value
                  }).then(function() {
                    console.log("Document successfully written!");
                })

              })
              console.log("success")
          } catch (error) {
            alert(error);
          }
    }
    return (<div>
        <Header></Header>
        <div className="SignupDiv">
            <h1>Sign Up</h1>
            <form onSubmit={handleSignUp}>
            <input name="fullname" type="text" placeholder="Full Name"></input>
            <input name="email" type="email" placeholder="Email"></input>
            <input name="gender"placeholder="You are"></input>
            <input name="college"placeholder="Your College"></input>
            <input name = "designation" placeholder="Your designation"></input>
            <input name="password" type="password" placeholder="Password"></input>
            <button type="submit" >Sign Up</button>
            </form>
        </div>
    </div>);
}

export default withRouter(Signup);


